'use strict';

var DIR_ROOT = __dirname + '/..';
var path = require('path');

var currify = require('currify/legacy');
var express = require('express');
var Router = express.Router;

var packer = require('./packer');

var salamFn = currify(_salamFn);

module.exports = function () {
    var options = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : {};

    var router = Router();
    var prefix = options.prefix || '/salam';

    router.route(prefix + '/*').get(salamFn(options)).get(staticFn);

    return router;
};

module.exports.listen = function (socket, options) {
    if (!options) options = {};

    if (!options.prefix) options.prefix = 'salam';

    if (!options.root) options.root = '/';

    packer(socket, options);
};

function _salamFn(options, req, res, next) {
    var o = options || {};
    var prefix = o.prefix || '/salam';
    var url = req.url;

    if (url.indexOf(prefix)) return next();

    req.url = req.url.replace(prefix, '');

    if (/^\/salam\.js(\.map)?$/.test(req.url)) req.url = '/dist' + req.url;

    next();
}

function staticFn(req, res) {
    var file = path.normalize(DIR_ROOT + req.url);
    res.sendFile(file);
}